void yuv_filter::thread_ap_clk_no_reset_() {
    if ( ap_rst.read() == ap_const_logic_1) {
        ap_CS_fsm = ap_ST_st1_fsm_0;
    } else {
        ap_CS_fsm = ap_NS_fsm.read();
    }
    if ( ap_rst.read() == ap_const_logic_1) {
        grp_yuv_filter_rgb2yuv_fu_115_ap_start_ap_start_reg = ap_const_logic_0;
    } else {
        if ((esl_seteq<1,1,1>(ap_const_logic_1, ap_sig_cseq_ST_st1_fsm_0.read()) && 
             !esl_seteq<1,1,1>(ap_start.read(), ap_const_logic_0))) {
            grp_yuv_filter_rgb2yuv_fu_115_ap_start_ap_start_reg = ap_const_logic_1;
        } else if (esl_seteq<1,1,1>(ap_const_logic_1, grp_yuv_filter_rgb2yuv_fu_115_ap_ready.read())) {
            grp_yuv_filter_rgb2yuv_fu_115_ap_start_ap_start_reg = ap_const_logic_0;
        }
    }
    if ( ap_rst.read() == ap_const_logic_1) {
        grp_yuv_filter_yuv2rgb_fu_135_ap_start_ap_start_reg = ap_const_logic_0;
    } else {
        if (esl_seteq<1,1,1>(ap_const_logic_1, ap_sig_cseq_ST_st5_fsm_4.read())) {
            grp_yuv_filter_yuv2rgb_fu_135_ap_start_ap_start_reg = ap_const_logic_1;
        } else if (esl_seteq<1,1,1>(ap_const_logic_1, grp_yuv_filter_yuv2rgb_fu_135_ap_ready.read())) {
            grp_yuv_filter_yuv2rgb_fu_135_ap_start_ap_start_reg = ap_const_logic_0;
        }
    }
    if ( ap_rst.read() == ap_const_logic_1) {
        grp_yuv_filter_yuv_scale_fu_150_ap_start_ap_start_reg = ap_const_logic_0;
    } else {
        if (esl_seteq<1,1,1>(ap_const_logic_1, ap_sig_cseq_ST_st3_fsm_2.read())) {
            grp_yuv_filter_yuv_scale_fu_150_ap_start_ap_start_reg = ap_const_logic_1;
        } else if (esl_seteq<1,1,1>(ap_const_logic_1, grp_yuv_filter_yuv_scale_fu_150_ap_ready.read())) {
            grp_yuv_filter_yuv_scale_fu_150_ap_start_ap_start_reg = ap_const_logic_0;
        }
    }
    if ((esl_seteq<1,1,1>(ap_const_logic_1, ap_sig_cseq_ST_st4_fsm_3.read()) && !esl_seteq<1,1,1>(ap_const_logic_0, grp_yuv_filter_yuv_scale_fu_150_ap_done.read()))) {
        p_scale_height_reg_234 = grp_yuv_filter_yuv_scale_fu_150_ap_return_1.read();
        p_scale_width_reg_229 = grp_yuv_filter_yuv_scale_fu_150_ap_return_0.read();
    }
    if ((esl_seteq<1,1,1>(ap_const_logic_1, ap_sig_cseq_ST_st2_fsm_1.read()) && !esl_seteq<1,1,1>(ap_const_logic_0, grp_yuv_filter_rgb2yuv_fu_115_ap_done.read()))) {
        p_yuv_height_reg_209 = grp_yuv_filter_rgb2yuv_fu_115_ap_return_1.read();
        p_yuv_width_reg_204 = grp_yuv_filter_rgb2yuv_fu_115_ap_return_0.read();
    }
}

void yuv_filter::thread_ap_done() {
    if ((esl_seteq<1,1,1>(ap_const_logic_1, ap_sig_cseq_ST_st6_fsm_5.read()) && 
         !esl_seteq<1,1,1>(ap_const_logic_0, grp_yuv_filter_yuv2rgb_fu_135_ap_done.read()))) {
        ap_done = ap_const_logic_1;
    } else {
        ap_done = ap_const_logic_0;
    }
}

void yuv_filter::thread_ap_idle() {
    if ((!esl_seteq<1,1,1>(ap_const_logic_1, ap_start.read()) && 
         esl_seteq<1,1,1>(ap_const_logic_1, ap_sig_cseq_ST_st1_fsm_0.read()))) {
        ap_idle = ap_const_logic_1;
    } else {
        ap_idle = ap_const_logic_0;
    }
}

